package ir.lucifer.approject;

import androidx.appcompat.app.AlertDialog;
import androidx.appcompat.app.AppCompatActivity;

import android.app.Activity;
import android.content.DialogInterface;
import android.content.Intent;
import android.graphics.Bitmap;
import android.graphics.BitmapFactory;
import android.os.Bundle;
import android.util.Base64;
import android.view.View;
import android.widget.Button;
import android.widget.ImageView;
import android.widget.TextView;
import android.widget.Toast;

import com.google.gson.JsonObject;

import retrofit2.Call;
import retrofit2.Callback;
import retrofit2.Response;
import retrofit2.Retrofit;
import retrofit2.converter.gson.GsonConverterFactory;

public class ProductPage extends AppCompatActivity {

    public Button addProduct;
    public MainAPI mainAPI;
    public Activity activity;
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_product_page);

        TextView proName = findViewById(R.id.productname);
        TextView proPrice = findViewById(R.id.price);
        TextView proDes = findViewById(R.id.description);
        TextView proPhone = findViewById(R.id.phonenumber);
        ImageView proImage = findViewById(R.id.productimage);
        addProduct = findViewById(R.id.addproductbutton);
        activity = this;
        Retrofit test2 = new Retrofit.Builder().baseUrl(Controler.url)
                .addConverterFactory(GsonConverterFactory.create()).build();

        mainAPI = test2.create(MainAPI.class);


        String imageBit = "";
        byte[] dec = Base64.decode(imageBit, Base64.DEFAULT);
        Bitmap img = BitmapFactory.decodeByteArray(dec, 0, dec.length);
        proImage.setImageBitmap(img);
        Intent product = getIntent();

        proName.setText(product.getStringExtra("PRO_SUBJECT"));
        proPrice.setText(String.format("%,d", Integer.parseInt(product.getStringExtra("PRO_PRICE"))));
        proDes.setText(product.getStringExtra("PRO_DES"));
        proPhone.setText(product.getStringExtra("PRO_PHONE"));
        int proID = product.getIntExtra("PRO_ID", -1);

        addProduct.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                JsonObject obj1 = new JsonObject();
                obj1.addProperty("productID", Integer.toString(proID));
                obj1.addProperty("buyerToken", Controler.Token);
                AlertDialog.Builder builder = new AlertDialog.Builder(activity);

                builder.setTitle("هشدار");
                builder.setMessage("آیا از خرید این محصول اطمینان دارید؟");

                builder.setPositiveButton("بله", new DialogInterface.OnClickListener() {

                    public void onClick(DialogInterface dialog, int which) {
                        // Do nothing but close the dialog
                        Call<JsonObject> call = mainAPI.buyProduct(obj1);
                        call.enqueue(new Callback<JsonObject>() {
                            @Override
                            public void onResponse(Call<JsonObject> call, Response<JsonObject> response) {
                                if (response.isSuccessful()) {
                                    JsonObject obj = response.body();

                                    if (obj.get("code").getAsInt() == 200) {
                                        Toast.makeText(getApplicationContext(), "You buy this Item Successfully with UserName:" + obj.get("buyerID").getAsString(), Toast.LENGTH_SHORT).show();
                                    } else {
                                        Toast.makeText(getApplicationContext(), obj.get("msg").getAsString(), Toast.LENGTH_SHORT).show();
                                    }
                                } else {
                                    Toast.makeText(getApplicationContext(), "Error " + response.code(), Toast.LENGTH_SHORT).show();
                                }

                            }

                            @Override
                            public void onFailure(Call<JsonObject> call, Throwable t) {
                                Toast.makeText(getApplicationContext(), t.getMessage(), Toast.LENGTH_SHORT).show();
                            }
                        });
                        addProduct.setEnabled(false);
                        dialog.dismiss();
                    }
                });

                builder.setNegativeButton("خیر", new DialogInterface.OnClickListener() {

                    @Override
                    public void onClick(DialogInterface dialog, int which) {

                        // Do nothing
                        dialog.dismiss();
                    }
                });

                AlertDialog alert = builder.create();
                alert.show();

            }
        });
    }

}